mapper.select:
SELECT *,i.fname fitemname FROM tb_wps w INNER JOIN tb_insframework i on w.fitemid = i.fid LEFT JOIN tb_insframework ins ON ins.fid = i.fparent LEFT JOIN tb_insframework insf ON insf.fid = ins.fparent where 1=1
and (i.fid = #{parent} or ins.fid=#{parent} or insf.fid=#{parent} or insf.fparent=#{parent})
and ${str}

select tb_mainwps.FID childrenid,FWPSNum,Fweld_I,Fweld_V,Fweld_I_MAX,Fweld_I_MIN,Fweld_V_MAX,Fweld_V_MIN,Fweld_Alter_I,Fweld_Alter_V,Fweld_PreChannel,FCReateDate,FUpdateDate fupdatedate,Fowner insid,Fback fback,Fname,Fdiameter fchildren_diameter,Fwelding_method,Ftype,Fpolarity,Fwelding_speed,Fspecification fchildren_specification from tb_mainwps where 1=1
and fwpsnum = #{wpsnum}
ORDER by tb_mainwps.FID

select tb_mainwps.FID 
childrenid,FWPSNum,Fweld_I,Fweld_V,Fweld_I_MAX,Fweld_I_MIN,Fweld_V_MAX,Fweld_V_MIN,Fweld_Alter_I,Fweld_Alter_V,Fweld_PreChannel,FCReateDate,FUpdateDate fupdatedate,Fowner insid,Fback fback,Fname,Fdiameter fchildren_diameter,Fwelding_method,Ftype,Fpolarity,Fwelding_speed,Fspecification fchildren_specification	from tb_mainwps 
where tb_mainwps.FID=#{childrenid}
